var express = require('express');
var Model = require('backbone-model').Model;
var Collect = require('backbone-collection').Collection;

var PlaceModel = Model.extend({
  defaults : {
    id        : "",
    title     : "",
    parent    : "",
    lft       : "",
    rgt       : "",
  }
});

var PlaceCollect = Collect.extend({
  model : PlaceModel
});

PlaceCollect = new PlaceCollect();

PlaceCollect.add([
  {id : 10 , title : "aaaaaaa"},
  {id : 11 , title : "qwqwqwwq"},
  {id : 12 , title : "asasasa"},
])

var Place = {
  _model    : PlaceModel,
  _collect  : PlaceCollect,
  
  config    : {
      name    : "Места",
      h1      : "Места",
  },
  
  getFilter : function(){
    return this._collect;
  }
}

module.exports = Place;